\DeclareSymbolFontAlphabet{\mathLINOTEXT}{linotext}
\begin{document}
$${\rm{MAD = }}{{\rm{1}} \over {\rm{N}}}\sum\limits_{{\rm{i = 1}}}^{\rm{N}} {\left| {{{\rm{X}}_{\rm{i}}}{\rm{ - }}{{\widehat {\rm{X}}}_{\rm{i}}}} \right|} $$
\end{document}
